import React, {useState} from 'react';
import { 
  StyleSheet, 
  Text, 
  View, 
  } from 'react-native';
import { ScrollView } from 'react-native-web';

const App = () => {
  const [Items, setItems] = useState([
    {key:1, item: 'Item 1'},
    {key:2, item: 'Item 2'},
    {key:3, item: 'Item 3'},
    {key:4, item: 'Item 4'},
    {key:5, item: 'Item 5'},
    {key:6, item: 'Item 6'},
    {key:7, item: 'Item 7'},
    {key:8, item: 'Item 8'},
  ])

  return (
    <ScrollView horizontal style={styles.body}>
      {
        Items.map((object) => {
          return(
            <View style={styles.item} key={object.key}>
            <Text style={styles.text}>{object.item}</Text>
            </View>

          )
        })
      }
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  body: {
    flex:1,
    flexDirection: 'column',
    backgroundColor: '#0000ff',

  },
  item: {
    margin:10,
    backgroundColor: '#00fe00',
    justifyContent: 'center',
    alignItems:'center',
  },

  text: {
    color: '#ffff',
    fontSize: 100 ,
    fontStyle:'italic',
    textTransform: 'uppercase',

  },


});
export default App;
