import React, {useState} from 'react';
import { 
  StyleSheet, 
  Text, 
  View, 
  TextInput,
  } from 'react-native';

const App = () => {

  const [name, setName] = useState('');

  return (
    <View style={styles.body}>
      <Text>
        Please Write your name:
      </Text>
      <TextInput style={styles.input} placeholder = 'enter here' onChangeText={(value) => setName(value)}>
      </TextInput>
      <Text style={styles.text}>
        Your name is: {name}
      </Text>


    </View>
    );
  };

const styles = StyleSheet.create({
  body: {
    flex:1,
    backgroundColor: '#0000ff',
    alignItems: 'center',
    justifyContent: 'center',

  },
  input: {
    borderWidth:1,


  },

  text: {
    color: '#ffff',
    fontSize: 20 ,
    margin: 10,

  },
});

export default App;
