import React, {useState, useEffect, useContext} from 'react';
import {
    View,
    Text,
    ImageBackground,
    TouchableOpacity,
    StyleSheet,
    TextInput,
    Alert,
  } from 'react-native';
  import FontAwesome5 from 'react-native-vector-icons/FontAwesome5';

  import Animated from 'react-native-reanimated';
  import BottomSheet from 'reanimated-bottom-sheet';
  import * as ImagePicker from 'expo-image-picker';
  import { collection, getFirestore, doc, getDoc, updateDoc } from "firebase/firestore";   
  import { getAuth } from "firebase/auth";
  import {ref, getStorage, uploadBytesResumable, getDownloadURL} from 'firebase/storage'


export default function EditProfilePage({ navigation }) {
  let auth = getAuth();
  const db = getFirestore();
  const storage = getStorage();

  const [image, setImage] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [transferred, setTransferred] = useState(0);
  const [userData, setUserData] = useState(null);
  const [camStatus, camRequestPermission] = ImagePicker.useCameraPermissions();
  const [libStatus, libRequestPermission] = ImagePicker.useMediaLibraryPermissions();

  const getUser = async() => {
      const docRef = doc(collection(db, "users"), auth.currentUser.uid);
      const docSnap = await getDoc(docRef);
      if( docSnap.exists() ) {
          console.log('User Data:', docSnap.data());
          setUserData(docSnap.data());
      } else {
          console.log('No such User Document')
      }
    }
  const handleUpdate = async() => {
    let imgUrl = await uploadImage();
    
    if( imgUrl == null && userData.profilePic ) {
      imgUrl = userData.profilePic;
    }
    const docRef = doc(collection(db, "users"), auth.currentUser.uid);
    updateDoc(docRef, {
      displayName: userData.displayName,
      NUSModsLink: userData.NUSModsLink,
      yearMajor: userData.yearMajor,
      profilePic: imgUrl,
      telegramHandle: userData.telegramHandle,
      userStatus: userData.userStatus,
    })
    .then(() => {
      console.log('User Updated!');
      Alert.alert(
        'Profile Updated!',
        'Your profile has been updated successfully.'
      );
    })
  }
  
    const uploadImage = async () => {
      if( image == null ) {
        return null;
      }
      const uploadUri = image;
      let filename = uploadUri.substring(uploadUri.lastIndexOf('/') + 1);

      // Add timestamp to File Name
      const extension = filename.split('.').pop(); 
      const name = filename.split('.').slice(0, -1).join('.');
      filename = name + Date.now() + '.' + extension;

      setUploading(true);
      setTransferred(0);
      const storageRef = ref(storage, 'photos/' + filename)
      
      const img = await fetch(uploadUri);
      const blob = await img.blob();

      const uploadTask = uploadBytesResumable(storageRef, blob);
      uploadTask.on( 'state_changed', 
        (snapshot) => {
          const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log('Upload is ' + progress + '% done');
          switch (snapshot.state) {
            case 'paused':
              console.log('Upload is paused');
              break;
            case 'running':
              console.log('Upload is running');
              break;
          }
        },
        (error) => {
          console.log(error)
          console.log('Upload is unsuccessful')
        }, 
        () => {
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            console.log('File available at', downloadURL);
          });
        }
      );
  
      try {
        await uploadTask;
  
        const url = await getDownloadURL(storageRef);
  
        setUploading(false);
        setImage(null);
         Alert.alert(
           'Image uploaded!',
           'Your image has been uploaded to the Firebase Cloud Storage Successfully!',
         );
        return url;
  
      } catch (e) {
        console.log(e);
        return null;
      }
  
    };
  
    useEffect(() => {
      getUser();
    }, []);
    
    const takePhotoFromCamera = () => {
      if (!camStatus) {
        camRequestPermission();
      } 
      if (camStatus) {
        ImagePicker.launchCameraAsync({
          allowsEditing: true,
          quality: 0.7,
        }).then((image) => {
          console.log(image);
          const imageUri = image.uri;
          setImage(imageUri);
          bs.current.snapTo(1);
        });
      } else {
        alert('No Permissions Granted');
        console.log('No Permissions Granted');
      }

    };

    const choosePhotoFromLibrary = () => {
      if (!libStatus) {
        libRequestPermission();
      }
      if (libStatus) {
        ImagePicker.launchImageLibraryAsync({
          allowsEditing: true,
          quality: 0.7,
        }).then((image) => {
          console.log(image);
          const imageUri = image.uri;
          setImage(imageUri);
          bs.current.snapTo(1);
        });
      } else {
        alert('No Permissions Granted');
        console.log('No Permissions Granted');
      }
    };

    renderInner = () => (
      <View style={styles.panel}>
        <View style={{alignItems: 'center'}}>
          <Text style={styles.panelTitle}>Upload Photo</Text>
          <Text style={styles.panelSubtitle}>Choose Your Profile Picture</Text>
        </View>
        <TouchableOpacity
          style={styles.panelButton}
          onPress={takePhotoFromCamera}>
          <Text style={styles.panelButtonTitle}>Take Photo</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.panelButton}
          onPress={choosePhotoFromLibrary}>
          <Text style={styles.panelButtonTitle}>Choose From Library</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.panelButton}
          onPress={() => bs.current.snapTo(1)}>
          <Text style={styles.panelButtonTitle}>Cancel</Text>
        </TouchableOpacity>
      </View>
    );
  
    renderHeader = () => (
      <View style={styles.header}>
        <View style={styles.panelHeader}>
          <View style={styles.panelHandle} />
        </View>
      </View>
    );
    
    bs = React.createRef();
    fall = new Animated.Value(1);

    return (
      <View style={styles.container}>
      <BottomSheet
        ref={bs}
        snapPoints={[330, -5]}
        renderContent={renderInner}
        renderHeader={renderHeader}
        initialSnap={1}
        callbackNode={fall}
        enabledGestureInteraction={true}
      />
      <Animated.View
        style={{
          margin: 20,
          opacity: Animated.add(0.1, Animated.multiply(fall, 1.0)),
        }}>
        <View style={{alignItems: 'center', paddingTop: 40}}>
          <TouchableOpacity onPress={() => bs.current.snapTo(0)}>
            <View
              style={{
                height: 100,
                width: 100,
                borderRadius: 15,
                justifyContent: 'center',
                alignItems: 'center',
              }}>
              <ImageBackground
                source={{
                  uri: image
                    ? image
                    : userData
                    ? userData.profilePic ||
                      'data:image/jpeg;base64,'
                    : 'data:image/jpeg;base64,',
                }}
                style={{height: 100, width: 100}}
                imageStyle={{borderRadius: 15}}>
                <View
                  style={{
                    flex: 1,
                    justifyContent: 'center',
                    alignItems: 'center',
                  }}>
                  <FontAwesome5
                    name="camera"
                    size={35}
                    color="#fff"
                    style={{
                      opacity: 0.7,
                      alignItems: 'center',
                      justifyContent: 'center',
                      borderWidth: 1,
                      borderColor: '#fff',
                      borderRadius: 10,
                    }}
                  />
                </View>
              </ImageBackground>
            </View>
          </TouchableOpacity>
          <Text style={{marginTop: 10, fontSize: 18, fontWeight: 'bold'}}>
            {userData ? userData.displayName : ''}
          </Text>
          {/* <Text>{user.uid}</Text> */}
        </View>

        <View style={styles.action}>
          <FontAwesome5 name="user" color="#333333" size={20} style={[{alignSelf: 'center'}]}/>
          <TextInput
            placeholder="Display Name"
            placeholderTextColor="#666666"
            value={userData ? userData.displayName : ''}
            onChangeText={(txt) => setUserData({...userData, displayName: txt})}
            autoCorrect={false}
            style={styles.textInput}
          />
        </View>
        <View style={styles.action}>
          <FontAwesome5 name="calendar" color="#333333" size={20} style={[{alignSelf: 'center'}]} />
          <TextInput
            multiline
            numberOfLines={3}
            placeholder="Status"
            placeholderTextColor="#666666"
            value={userData ? userData.userStatus : ''}
            onChangeText={(txt) => setUserData({...userData, userStatus: txt})}
            autoCorrect={true}
            style={[styles.textInput, {height: 40}]}
          />
        </View>
        <View style={styles.action}>
          <FontAwesome5 name="user-graduate" color="#333333" size={20} style={[{alignSelf: 'center'}]} />
          <TextInput
            placeholder="Year, Major"
            placeholderTextColor="#666666"
            value={userData ? userData.yearMajor : ''}
            onChangeText={(txt) => setUserData({...userData, yearMajor: txt})}
            autoCorrect={false}
            style={styles.textInput}
          />
        </View>
        <View style={styles.action}>
          <FontAwesome5 name="telegram" color="#333333" size={20} style={[{alignSelf: 'center'}]} />
          <TextInput
            placeholder="Telegram Handle"
            placeholderTextColor="#666666"
            value={userData ? userData.telegramHandle : ''}
            onChangeText={(txt) => setUserData({...userData, telegramHandle: txt})}
            autoCorrect={false}
            style={styles.textInput}
          />
        </View>
        <TouchableOpacity
                style={styles.userBtn}
                onPress={handleUpdate}>
                <Text style={styles.userBtnTxt}>Update</Text>
        </TouchableOpacity>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  commandButton: {
    padding: 15,
    borderRadius: 10,
    backgroundColor: '#FF6347',
    alignItems: 'center',
    marginTop: 10,
  },
  panel: {
    padding: 20,
    backgroundColor: '#FFFFFF',
    paddingTop: 20,
    width: '100%',
  },
  header: {
    backgroundColor: '#FFFFFF',
    shadowColor: '#333333',
    shadowOffset: {width: -1, height: -3},
    shadowRadius: 2,
    shadowOpacity: 0.4,
    paddingTop: 20,
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
  },
  panelHeader: {
    alignItems: 'center',
  },
  panelHandle: {
    width: 40,
    height: 8,
    borderRadius: 4,
    backgroundColor: '#00000040',
    marginBottom: 10,
  },
  panelTitle: {
    fontSize: 27,
    height: 35,
  },
  panelSubtitle: {
    fontSize: 14,
    color: 'gray',
    height: 30,
    marginBottom: 10,
  },
  panelButton: {
    padding: 13,
    borderRadius: 10,
    backgroundColor: '#2e64e5',
    alignItems: 'center',
    marginVertical: 7,
  },
  panelButtonTitle: {
    fontSize: 17,
    fontWeight: 'bold',
    color: 'white',
  },
  action: {
    flexDirection: 'row',
    marginTop: 10,
    marginBottom: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#f2f2f2',
    paddingBottom: 5,
  },
  actionError: {
    flexDirection: 'row',
    marginTop: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#FF0000',
    paddingBottom: 5,
  },
  textInput: {
    flex: 1,
    height: 30,
    marginTop: 0,
    paddingLeft: 15,
    color: '#333333',
  },
  userBtn: {
    alignSelf: 'center',
    width: 80,
    borderColor: '#2e64e5',
    borderWidth: 2,
    borderRadius: 3,
    paddingVertical: 8,
    paddingHorizontal: 12,
    marginHorizontal: 5,
  },
  userBtnTxt: {
    textAlign: 'center',
    color: '#2e64e5',
  },
});